INSERT INTO `goods` VALUES ('127', '38', '城市软叶绿生菜', '0', '25.00', '310000000', '散', '', '', '<p>\r\n	城市蔬菜，来自于城市超市5000亩直营农场，精心培育，新鲜送达，品质值得信赖。</p>\r\n<p>\r\n	软叶绿叶生菜适合搭配其他蔬菜、肉类、海鲜等，做成一道营养美味的色拉。生菜中的膳食纤维、维生素C帮助消除多余脂肪。由于软叶生菜的叶片较其它生菜柔软，适合做各类生菜包。</p>\r\n<div class=\"recipe\">\r\n	<h5 class=\"recipe-name\">\r\n		广西生菜包（3人份）</h5>\r\n	<dl>\r\n		<dt>\r\n			原料：</dt>\r\n		<dd>\r\n			球生菜 5-6片，鸡肉 100克，泡发香菇 2-3个，竹笋 1/3根，西芹 1根，松子 少许，胡萝卜 1/4根</dd>\r\n		<dt>\r\n			调味料：</dt>\r\n		<dd>\r\n			料酒、盐、胡椒粉、糖、鸡精、酱油、姜丝、葱段、生粉、香油 适量</dd>\r\n		<dt>\r\n			制作方法：</dt>\r\n		<dd>\r\n			<ol>\r\n				<li>\r\n					鸡肉洗净、切丁，加入姜丝、葱段、料酒、盐、酱油、生粉拌匀，腌制片刻。</li>\r\n				<li>\r\n					将香菇、竹笋、西芹、胡萝卜分别洗净切丁，焯水捞出。锅中入油烧热，将松子爆香盛出，再倒入1中腌制好的鸡肉炒至变色，加入之前焯熟的食材，翻炒后加入少许盐、糖、胡椒粉、鸡精调味，出锅时淋上香油。</li>\r\n				<li>\r\n					取大小适中的生菜叶，将2中炒好的食材盛放其中，撒上松子即可。</li>\r\n			</ol>\r\n		</dd>\r\n	</dl>\r\n</div>', '1', '2017-03-19 00:08:11', '127', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20171506', '1', '');
