INSERT INTO `goods` VALUES ('138', '39', '冬瓜', '0', '8.00', '310000000', 'kg', '', '', '<style type=\"text/css\">\r\n#desc-wrapper{width: 880px;margin: 0 auto;}#desc-wrapper img{vertical-align: top;}#desc-wrapper .clearfix{overflow: hidden;}#desc-wrapper .col-lg-2{float: left;width: 50%;}#desc-wrapper .col-lg-2 img{margin-bottom: 5px;}#desc-wrapper .section{margin-bottom: 0; padding: 0 15px;}#desc-wrapper .section .headline{margin-top: 0;margin-bottom: 10px;font-size: 20px;color:#4b7f02;}#desc-wrapper .section .subtitle{margin-top:0;margin-bottom:5px;font-size:16px;color:#4b7f02;}#desc-wrapper .section p{margin-top: 0;margin-bottom: 18px;font-size: 13px;line-height: 1.538461em;}#desc-wrapper .recipe{margin-top: 25px;}#desc-wrapper .recipe-name{margin-top: 0;margin-bottom: 10px;font-size: 16px;color: #e58900;}#desc-wrapper .recipe-detail{margin-top: 0;margin-bottom: 0;}#desc-wrapper .recipe-detail dt{margin-top: 8px;margin-bottom: 4px;font-size: 14px;font-weight: bold;color: #e58900;}#desc-wrapper .recipe-detail dd{margin-left: 0;font-size: 13px;line-height: 1.538461em; }#desc-wrapper .recipe-detail ol{margin-top: 0;margin-bottom: 0;padding-left: 24px;}#desc-wrapper .recipe-detail ol li{margin-bottom: 4px;}</style>\r\n<div id=\"desc-wrapper\">\r\n	<div class=\"clearfix\">\r\n		<div class=\"col-lg-2\">\r\n			<div class=\"section\">\r\n				<h1 class=\"headline\">\r\n					冬瓜瘦身，有理有据</h1>\r\n				<p>\r\n					冬瓜又名枕瓜，它果肉洁白清凉，疏松多汁，有清热解毒、利水消痰、除烦止渴、祛湿解暑的功效，是夏季消暑减脂的最具代表性的食材。之所以名为&ldquo;冬&rdquo;瓜，大概是因为冬瓜成熟之际，果实表面会有一层白色粉状霜，像及了那冬天结下的霜。</p>\r\n				<p>\r\n					冬瓜是东方人的食材，西方料理中罕见。提到冬瓜当然不能落下广东名菜&ldquo;冬瓜蛊&rdquo;了，它采用冬瓜当容器，成菜后汤中有冬瓜的清香，冬瓜肉里有馅料的味道，互相映衬，可谓夏日的应时佳肴。</p>\r\n				<h2 class=\"subtitle\">\r\n					脂肪克星</h2>\r\n				<p>\r\n					《食疗本草》里关于冬瓜有这样的记载：欲得体瘦轻者，则可常食之。关于冬瓜减肥，是有科学依据的。冬瓜中富含丙醇二酸，能有效防止体内的糖类转化为脂肪，还能把多余脂肪消耗掉，防止堆积，加之冬瓜本身不含脂肪，热量不高，因此自古都是减肥妙品。</p>\r\n				<p>\r\n					此外，冬瓜味甘性寒，含有丰富膳食纤维，可改善血糖水平，降低胆固醇，对糖尿病、动脉硬化及高血压患者都有良好的治疗辅助作用。</p>\r\n				<h2 class=\"subtitle\">\r\n					选购方法</h2>\r\n				<p>\r\n					挑选冬瓜时，应选择皮色青绿，带白霜，形状端正，表皮无斑点和外伤，且皮不软不腐烂的。</p>\r\n				<h2 class=\"subtitle\">\r\n					饮食宜忌</h2>\r\n				<p>\r\n					肾功能不良者、脾胃虚寒者和身体消瘦者忌食冬瓜。<br />\r\n					女子月经来潮期间和寒性痛经者忌食生冬瓜。</p>\r\n			</div>\r\n		</div>\r\n		<div class=\"col-lg-2\">\r\n			<img alt=\"冬瓜\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/WhiteGourdFigures001.jpg\" /> <img alt=\"冬瓜\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/WhiteGourdFigures002.jpg\" /></div>\r\n	</div>\r\n	<div class=\"recipe clearfix\">\r\n		<div class=\"col-lg-2\">\r\n			<img alt=\"冬瓜薏仁排骨汤\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/WhiteGourdFigures003.jpg\" /></div>\r\n		<div class=\"col-lg-2\">\r\n			<div class=\"section\">\r\n				<h1 class=\"recipe-name\">\r\n					【食单】冬瓜薏仁排骨汤</h1>\r\n				<dl class=\"recipe-detail\">\r\n					<dt>\r\n						原料</dt>\r\n					<dd>\r\n						排骨500克，冬瓜500克，薏仁50克，葱少许，姜少许，盐5克，料酒50毫升，生抽</dd>\r\n					<dt>\r\n						制作方法</dt>\r\n					<dd>\r\n						<ol class=\"recipe-progress\">\r\n							<li>\r\n								排骨洗净，焯水，洗净血沫；薏仁冲洗干净。</li>\r\n							<li>\r\n								排骨、薏仁、葱姜放入砂锅内，倒入50ml料酒，加足量水烧开，转小火慢炖1小时。</li>\r\n							<li>\r\n								将冬瓜洗净，去皮，切块，放入锅内继续炖至冬瓜熟软，关火前用盐调味即可。</li>\r\n						</ol>\r\n					</dd>\r\n				</dl>\r\n			</div>\r\n		</div>\r\n	</div>\r\n	<div class=\"recipe clearfix\">\r\n		<div class=\"col-lg-2\">\r\n			<img alt=\"冬瓜炒虾仁\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/WhiteGourdFigures004.jpg\" /></div>\r\n		<div class=\"col-lg-2\">\r\n			<div class=\"section\">\r\n				<h1 class=\"recipe-name\">\r\n					【食单】冬瓜炒虾仁</h1>\r\n				<dl class=\"recipe-detail\">\r\n					<dt>\r\n						原料</dt>\r\n					<dd>\r\n						冬瓜500克，虾仁250克，葱，大蒜，花椒，鸡精，料酒，盐，淀粉</dd>\r\n					<dt>\r\n						制作方法</dt>\r\n					<dd>\r\n						<ol class=\"recipe-progress\">\r\n							<li>\r\n								1. 冬瓜洗净，去皮，切块，用开水焯至七成熟。</li>\r\n							<li>\r\n								虾仁洗净，用料酒，盐，鸡精，淀粉抓匀，腌制片刻。</li>\r\n							<li>\r\n								起油锅，油热后放入虾仁滑炒，变色后盛出。</li>\r\n							<li>\r\n								锅中再放少许油加热，爆香花椒、葱和大蒜，放入冬瓜翻炒，用盐调味。</li>\r\n							<li>\r\n								最后放入虾仁炒匀即可。</li>\r\n						</ol>\r\n					</dd>\r\n				</dl>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>', '1', '2017-03-19 00:08:13', '138', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20178147', '1', '');
