INSERT INTO `goods` VALUES ('157', '42', '鲜富来绿豆芽', '0', '7.00', '310000000', '350g', '', '', '<style type=\"text/css\">\r\n#desc-wrapper{width: 880px;margin: 0 auto;}#desc-wrapper img{vertical-align: top;}#desc-wrapper .clearfix{overflow: hidden;}#desc-wrapper .col-lg-2{float: left;width: 50%;}#desc-wrapper .section{margin-bottom: 0; padding: 10px 15px;}#desc-wrapper .recipe-name{margin-top: 0;margin-bottom: 10px;font-size: 16px;color: #e58900;}#desc-wrapper .recipe-detail{margin-top: 0;margin-bottom: 0;}#desc-wrapper .recipe-detail dt{margin-top: 8px;margin-bottom: 4px;font-size: 14px;font-weight: bold;color: #e58900;}#desc-wrapper .recipe-detail dd{margin-left: 0;font-size: 13px;line-height: 1.538461em; }#desc-wrapper .recipe-detail ol{margin-top: 0;margin-bottom: 10px;padding-left: 24px;}#desc-wrapper .recipe-detail ol li{margin-bottom: 4px;}</style>\r\n<div id=\"desc-wrapper\">\r\n	<div class=\"recipe clearfix\">\r\n		<div class=\"col-lg-2\">\r\n			<div class=\"section\">\r\n				<h1 class=\"recipe-name\">\r\n					【食单】炒合菜</h1>\r\n				<dl class=\"recipe-detail\">\r\n					<dt>\r\n						原料</dt>\r\n					<dd>\r\n						鸡蛋4个，韭菜100克，绿豆芽100克，胡萝卜1根，粉丝1把，油，花雕，盐，生抽，花椒</dd>\r\n					<dt>\r\n						制作方法</dt>\r\n					<dd>\r\n						<ol class=\"recipe-progress\">\r\n							<li>\r\n								鸡蛋打散，调入适量盐和花雕；粉丝泡软，适当剪短。</li>\r\n							<li>\r\n								绿豆芽掐去两头，洗净；胡萝卜切丝；韭菜洗净，切段。</li>\r\n							<li>\r\n								起油锅，倒入所有蛋液摊成1个鸡蛋饼，凝固后盛出，摆入盘中，略厚的蛋饼更易吸收汤汁。</li>\r\n							<li>\r\n								锅中再倒油，煸香几粒花椒，下胡萝卜丝翻炒，烹入少许花雕和生抽，然后依次下粉丝、绿豆芽和韭菜，用盐调味，连同汤汁一起盛在蛋饼上即可。</li>\r\n						</ol>\r\n					</dd>\r\n				</dl>\r\n				<img alt=\"炒合菜\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/MungbeanSproutsFigures001.jpg\" /></div>\r\n		</div>\r\n		<div class=\"col-lg-2\">\r\n			<div class=\"section\">\r\n				<h1 class=\"recipe-name\">\r\n					【食单】凉拌三色菜</h1>\r\n				<img alt=\"凉拌三色菜\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/MungbeanSproutsFigures002.jpg\" />\r\n				<dl class=\"recipe-detail\">\r\n					<dt>\r\n						原料</dt>\r\n					<dd>\r\n						绿豆芽100克，黄瓜1根，胡萝卜1根，熟白芝麻少许，盐，生抽，米醋，辣椒油，香油</dd>\r\n					<dt>\r\n						制作方法</dt>\r\n					<dd>\r\n						<ol class=\"recipe-progress\">\r\n							<li>\r\n								绿豆芽掐去两头，择洗干净；胡萝卜、黄瓜洗净切丝。</li>\r\n							<li>\r\n								绿豆芽、胡萝卜入沸水汆熟，捞出后浸凉水，沥干。</li>\r\n							<li>\r\n								所有食材放入碗中，用适量盐、生抽、米醋和少许辣椒油拌匀，最后撒上熟白芝麻，淋上香油即可。</li>\r\n						</ol>\r\n					</dd>\r\n				</dl>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>', '1', '2017-03-19 00:08:18', '157', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20565657', '1', '');
