INSERT INTO `goods` VALUES ('247', '48', 'KBR澳洲谷饲去骨牛肋排', '0', '260.00', '833100000', '约 300g/盒', '', '', '<style type=\"text/css\">\r\n.STYLE1 {\r\n	font-size: 18px;\r\n	font-weight: bold;\r\n	color: #f7941d;}\r\n.STYLE2 {\r\n	font-weight: bold;\r\n	color: rgb(165, 42, 42);\r\n}\r\n.STYLE3 {\r\n	font-weight: bold;\r\ncolor: rgb(139, 69, 19);\r\n}</style>\r\n<!--<p style=\"color:red;margin: 15px 0 5px;font-size:14px;\">\r\n	澳洲牛肉全场<span style=\"font-size: 16px;font-weight:bold;\">八折</span>特惠<br />\r\n	活动时间：2014.10.18-11.2</p>\r\n<p style=\"color:gray;margin:0;\">\r\n	*页面显示价格已为折扣价</p>--><br />\r\n<span class=\"STYLE1\">产品特色</span>\r\n<p>\r\n	澳洲牛肉根据饲养方式的差异分为谷饲牛肉和草饲牛肉两大类。草饲牛达到一定重量后，集中以高营养谷物饲养一定天数的牛称之为谷饲牛，其出产的牛肉就是广受食客追捧的谷饲牛肉。由于谷物饲料由大麦、燕麦、玉米、高粱等作物组成，所以澳洲谷饲牛肉脂肪含量较高，有华丽的大理石花纹状脂肪分布于肌肉纹理之间。口感仅次于和牛，脂膏肥嫩，适口性佳。</p>\r\n<div style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/asbeef.jpg\" style=\"width: 500px; height: 330px;\" /><br />\r\n	<span class=\"STYLE3\">澳洲谷饲牛肉有着非常精细的分级制度。<br />\r\n	谷物饲养时间越长，牛肉中脂肪形成的大理石花纹就愈发绵密，肉质也更为丰腴。<br />\r\n	我们推荐以谷物饲养60天和100天的牛肉，您可依据个人偏好和烹饪方法进行选购。</span></div>\r\n<br />', '1', '2017-03-19 00:08:28', '247', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20319595', '1', '');
