INSERT INTO `goods` VALUES ('253', '48', 'KBR澳洲谷饲牛眼肉排', '0', '450.00', '833100000', '约200g/盒', '', '', '<link href=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/css/desctiption.css\" rel=\"stylesheet\" />\r\n<div id=\"desc-container\">\r\n	<div class=\"section\">\r\n		<h4 class=\"st\">\r\n			产品特色</h4>\r\n		<p>\r\n			澳洲牛肉根据饲养方式的差异分为谷饲牛肉和草饲牛肉两大类。草饲牛达到一定重量后，集中以高营养谷物饲养一定天数的牛称之为谷饲牛，其出产的牛肉就是广受食客追捧的谷饲牛肉。由于谷物饲料由大麦、燕麦、玉米、高粱等作物组成，所以澳洲谷饲牛肉脂肪含量较高，有华丽的大理石花纹状脂肪分布于肌肉纹理之间。口感仅次于和牛，脂膏肥嫩，适口性佳。</p>\r\n	</div>\r\n	<div class=\"center\">\r\n		<img alt=\"牛肉分解图\" class=\"figures1\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/asbeef.jpg\" />\r\n		<p class=\"c-brown bold\">\r\n			澳洲谷饲牛肉有着非常精细的分级制度。<br />\r\n			谷物饲养时间越长，牛肉中脂肪形成的大理石花纹就愈发绵密，肉质也更为丰腴。<br />\r\n			我们推荐以谷物饲养60天和100天的牛肉，您可依据个人偏好和烹饪方法进行选购。</p>\r\n	</div>\r\n</div>', '1', '2017-03-19 00:08:28', '253', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20702397', '1', '');
