INSERT INTO `goods` VALUES ('283', '54', '小南国招牌虾仁', '0', '68.00', '320900000', '225g', '', '', '<link href=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/css/desctiption.css\" rel=\"stylesheet\" />\r\n<div id=\"desc-container\">\r\n	<div class=\"section\">\r\n		<h4 class=\"st\">\r\n			赏味有道</h4>\r\n		<p>\r\n			小南国招牌虾仁选材产自素有&ldquo;鱼米之乡&rdquo;之称的江苏，清明透亮、鲜甜可口。只需经过最简单的烹饪，便可享受河虾的鲜爽清甜原味在舌尖绽放。</p>\r\n	</div>\r\n	<div class=\"cs-recipe\">\r\n		<h4 class=\"st\">\r\n			【烹饪方法】</h4>\r\n		<h5 class=\"cs-recipe-name\">\r\n			滑蛋虾仁</h5>\r\n		<dl>\r\n			<dt>\r\n				原料：</dt>\r\n			<dd>\r\n				虾仁200克，鸡蛋2只</dd>\r\n			<dt>\r\n				调味料：</dt>\r\n			<dd>\r\n				姜末，料酒，白酒，纯牛奶，橄榄油，盐</dd>\r\n			<dt>\r\n				制作方法：</dt>\r\n			<dd>\r\n				<ol>\r\n					<li>\r\n						虾仁解冻后洗净，沥干多余水分备用。</li>\r\n					<li>\r\n						料酒、盐、白酒少许混合拌匀，将虾仁倒入其中腌制10分钟。</li>\r\n					<li>\r\n						蛋液中加入30克牛奶和少许盐，打匀备用。</li>\r\n					<li>\r\n						起油锅加热，油温加热至6成热时放入姜末煸香。</li>\r\n					<li>\r\n						倒入腌好的虾仁，大火翻炒。</li>\r\n					<li>\r\n						虾仁稍微变色时加入蛋液，小火滑炒至蛋液凝固即可关火出锅。</li>\r\n				</ol>\r\n			</dd>\r\n		</dl>\r\n	</div>\r\n</div>', '1', '2017-03-19 00:08:37', '283', '1', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20799915', '1', '');
