INSERT INTO `goods` VALUES ('288', '54', '北极甜虾（80-100）', '0', '38.00', '830400000', '200g', '', '', '<link href=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/css/desctiption.css\" rel=\"stylesheet\" />\r\n<div id=\"desc-container\">\r\n	<div class=\"section\">\r\n		<h4 class=\"st\">\r\n			赏味有道</h4>\r\n		<p>\r\n			北极甜虾产自北极附近海域，滋味清甜，因而得名。虾肉呈粉红色，肉质紧密。室温下自然解冻后，可简单焯过即食，可与鲜蔬制成沙拉，也可去皮炒制。</p>\r\n	</div>\r\n	<div class=\"cs-recipe\">\r\n		<h4 class=\"st\">\r\n			【烹饪方法】</h4>\r\n		<h5 class=\"cs-recipe-name\">\r\n			剁椒北极虾</h5>\r\n		<dl>\r\n			<dt>\r\n				原料：</dt>\r\n			<dd>\r\n				北极虾500克，剁椒2大匙</dd>\r\n			<dt>\r\n				调味料：</dt>\r\n			<dd>\r\n				葱花，蒜末，姜片，白糖1小匙，盐适量</dd>\r\n			<dt>\r\n				制作方法：</dt>\r\n			<dd>\r\n				<ol>\r\n					<li>\r\n						北极虾解冻后，剪去须爪，洗净后沥干多余水分，摆盘。</li>\r\n					<li>\r\n						另取一碗，放入剁椒，葱花、蒜末、白糖、盐、黄酒，搅拌均匀。</li>\r\n					<li>\r\n						在虾上铺好姜片，然后将步骤2的调料均匀铺在上面。</li>\r\n					<li>\r\n						入蒸锅大火蒸10分钟出锅即可。</li>\r\n				</ol>\r\n			</dd>\r\n		</dl>\r\n	</div>\r\n</div>', '1', '2017-03-19 00:08:37', '288', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20761509', '1', '');
