INSERT INTO `goods` VALUES ('309', '55', '水库鳙鱼中段', '0', '70.00', '0', '散', '', '', '<style type=\"text/css\">\r\n.STYLE1 {\r\n	font-size: 18px;\r\n	font-weight: bold;\r\n	color: #f7941d;}\r\n.STYLE2 {\r\n	font-size: 14px;\r\n	font-weight: bold;}</style>\r\n<span class=\"STYLE1\">赏味有道</span>\r\n<div align=\"center\">\r\n	<img alt=\"\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/20758998_2.jpg\" style=\"width: 485px; height: 364px;\" /></div>\r\n<p style=\"text-indent: 2em;\">\r\n	鳙鱼，又名花鲢，对脾胃虚寒、体质虚弱的人而言是绝佳的温补食材，常食可暖胃化痰，润泽皮肤，延缓衰老。鳙鱼中段肥美，可清蒸，亦可炸制成酥鱼。</p>\r\n<span class=\"STYLE1\">产区介绍</span>\r\n<p style=\"text-indent: 2em;\">\r\n	<span class=\"STYLE2\">天赐好水：</span>植被掩映中的太平湖水库水若明镜，群山似翠。出色的水质和空气赋予了太平湖水库鱼特殊的鲜甜，绝无泥腥味的困扰。</p>\r\n<p style=\"text-indent: 2em;\">\r\n	<span class=\"STYLE2\">生态养殖：</span>太平湖水库鱼只依靠水中微生物为生，近乎野生的养殖方式造就了鱼肉滑嫩紧致的口感，烹饪时只需简单调味，即便是家常菜式也保证好吃到停不了口！</p>\r\n<p style=\"text-indent: 2em;\">\r\n	<span class=\"STYLE2\">新鲜速递：</span>水库鱼和太平湖的水一起装进专业鱼桶，全程加氧运输，保证水库鱼经过5小时运送抵沪后依然鲜活如初。</p>\r\n<div align=\"center\">\r\n	<img alt=\"\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/20758998_3.jpg\" style=\"width: 485px; height: 302px;\" /></div>\r\n<span class=\"STYLE1\">烹饪方法</span><br />\r\n<br />\r\n<div align=\"center\">\r\n	<img alt=\"\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/20758998-cai.jpg\" style=\"width: 414px; height: 223px;\" /></div>\r\n<br />\r\n<span class=\"STYLE2\">啤酒鱼</span><br />\r\n<span class=\"STYLE2\">原料：</span>鳙鱼中段500克，啤酒350毫升，圣女果数粒<br />\r\n<span class=\"STYLE2\">调味料：</span>盐，生姜，红椒，花椒，酱油，白糖<br />\r\n<span class=\"STYLE2\">制作方法：</span>\r\n<ol>\r\n	<li>\r\n		将处理干净的鳙鱼中段切成宽约3厘米的鱼块，晾干水分备用。</li>\r\n	<li>\r\n		鱼块入油锅，中火煎至微黄。</li>\r\n	<li>\r\n		加入切好的姜丝、红椒圈、花椒，淋入1:1比例的啤酒和清水，水位以没过材料为宜。</li>\r\n	<li>\r\n		水烧开后，加入适量酱油、白糖、盐，小火煮15分钟，待汤汁收浓关火，撒上少许葱花、圣女果装饰摆盘即可。</li>\r\n</ol>', '1', '2017-03-19 00:08:39', '309', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20758998', '1', '黄山太平湖');
