INSERT INTO `goods` VALUES ('371', '62', '清美绢豆腐', '0', '2.60', '310000000', '350克', '', '', '<link href=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/css/description2.css\" rel=\"stylesheet\" />\r\n<div id=\"desc-container\">\r\n	<div class=\"section\">\r\n		<img alt=\"清美牌\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/TramyFigures001-cn.jpg\" /></div>\r\n	<div class=\"section\">\r\n		<h4 class=\"st\">\r\n			赏味有道</h4>\r\n		<p>\r\n			豆制品的品种极为丰富豆制品的品种极为丰富，涵盖有豆腐、豆腐脑、素鸡、豆腐干、油条子、腐竹、豆皮等各种形态。其中最基本的形态是豆腐，相传是西汉淮南王刘安炼丹所得。</p>\r\n		<p>\r\n			豆腐价廉物美，营养丰富，分为南、北两大类型。南豆腐是将豆腐脑倒入布包经轻压后制成，多以石膏点制，色雪白，质细嫩，味略甜而鲜；北豆腐是将豆腐脑倒入模具，经紧压后制成，多以盐卤点制，色乳白，质较老，味微甜略苦，结构紧密，富有弹性，蛋白质含量较高。</p>\r\n		<p>\r\n			豆腐界的新宠绢豆腐在性状上则与南豆腐较为接近，如丝般细滑，清新白嫩。既适用于多种工艺加工和各种烹调方式，又适用于各种调味方法，并可用做主食、菜肴、小吃及馅料，深受食客青睐。</p>\r\n	</div>\r\n	<div class=\"cs-recipe\">\r\n		<img alt=\"豆腐\" class=\"figures figures-b\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/tofu002.jpg\" />\r\n		<h4 class=\"st\">\r\n			【城市厨房】</h4>\r\n		<h5 class=\"cs-recipe-name\">\r\n			金钩挂玉牌</h5>\r\n		<p>\r\n			黄豆芽为金钩，豆腐为玉牌，这道流行于云贵地区的家常菜有着别具诗意的名字，虽不见完整的豆子，却同时存在着两种由黄豆演化而来的制品，而且两者各自彰显个性又互相映衬，十分玄妙。而它的出处也十分有趣，相传，以前贵州有个读书人省试高中，主考官问及他的家事，考生回答：&ldquo;父，肩挑金钩玉牌沿街走；母，在家两袖清风挽转乾坤献琼浆&rdquo;，考官对于他的文采和幽默颇为欣赏，黄豆芽煮豆腐从此也有了金钩挂玉牌的雅号。</p>\r\n		<dl>\r\n			<dt>\r\n				制作方法：</dt>\r\n			<dd>\r\n				黄豆芽和豆腐放入锅中，加高汤煮制，待豆腐吸收了豆芽的滋味和香气便可关火上桌。一同出菜的必有一叠蘸汁，它的配方多种多样，最为传统的做法是以辣椒粉、花椒粉、酱油、鸡精浇上热油冲调而成，滋味麻辣咸香。</dd>\r\n		</dl>\r\n	</div>\r\n</div>', '1', '2017-03-19 00:08:49', '371', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20530907', '1', '');
