INSERT INTO `goods` VALUES ('529', '86', '康特瑞牌奥弗涅青纹干酪', '0', '52.00', '832700000', '125克', '', '', '<style type=\"text/css\">\r\n.STYLE1 {\r\n	font-size: 18px;\r\n	font-weight: bold;\r\n	color: #f7941d;}\r\n.STYLE2 {\r\n	font-weight: bold;}\r\n.STYLE3 {\r\n	font-size: 14px;\r\n	font-weight: bold;\r\n	color: rgb(204, 51, 51);}</style>\r\n<p>\r\n	<span class=\"STYLE2\">产地：</span>法国奥弗涅地区<br />\r\n	<span class=\"STYLE2\">类型：</span>蓝纹奶酪<br />\r\n	<span class=\"STYLE2\">奶源：</span>未经巴氏杀菌的鲜牛奶<br />\r\n	<span class=\"STYLE2\">脂肪含量：</span>45%</p>\r\n<span class=\"STYLE1\">商品描述</span>\r\n<p>\r\n	这种奥弗涅青纹奶酪有着淡黄色的外壳，干酪表面有地窖中的特殊气味，口感湿润光滑，有辛辣的味道和新鲜的奶油风味。</p>\r\n<span class=\"STYLE1\">品牌故事</span>\r\n<p>\r\n	1850年，生活在Auvergne一个农庄的Antoine Roussel注意到一些奶酪在地窖变成了蓝色，并散发着特殊的，愉快的香气，他意识到这种奶酪经历了非同一般的变化。经过多次实验，他终于发现了奶酪变成蓝色的秘密，并首次用黑麦面包上的霉菌制作这种独特的干酪，获得了巨大的成功。后来，该地区的许多奶酪生产商开始复制他的成功，为了纪念&ldquo;蓝纹奶酪&rdquo;这个伟大的发明，村子里的人们为Antoine Rousse 塑了一座雕像，如今人们还能看到它。</p>\r\n<span class=\"STYLE1\">食用方法</span>\r\n<ol>\r\n	<li>\r\n		奥弗涅青纹干酪非常适合制作沙拉酱和面食调味料。</li>\r\n	<li>\r\n		当作零食或甜点食用，可以配一些甜葡萄酒或深色啤酒来陪衬它的丰富，风味十分独特。</li>\r\n</ol>\r\n<br />', '1', '2017-03-19 00:09:23', '529', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20437442', '1', '');
