INSERT INTO `goods` VALUES ('563', '88', '安拉三角型蓝波芝士', '0', '29.80', '830100000', '100克', '', '', '<style type=\"text/css\">\r\n.STYLE1 {\r\n	font-size: 18px;\r\n	font-weight: bold;\r\n	color: #f7941d;}\r\n.STYLE2 {\r\n	font-weight: bold;}\r\n.STYLE3 {\r\n	font-size: 14px;\r\n	font-weight: bold;\r\n	color: rgb(204, 51, 51);}</style>\r\n<p>\r\n	<span class=\"STYLE2\">产地：</span>丹麦<br />\r\n	<span class=\"STYLE2\">奶源：</span>牛奶<br />\r\n	<span class=\"STYLE2\">类型：</span>蓝纹干酪<br />\r\n	<span class=\"STYLE2\">脂肪含量：</span>28.6%</p>\r\n<span class=\"STYLE1\">商品描述</span>\r\n<p>\r\n	安拉三角型蓝波芝士是始于1927年的经典蓝纹奶酪，与其它奶酪最大的区别是，使用均质牛乳制造使得口感更突出。拥有浓郁的辛辣和奶香，以及淡咸味。</p>\r\n<span class=\"STYLE1\">品牌故事</span>\r\n<p>\r\n	丹麦蓝纹干酪风味强烈，尝起来质地与奶油相似。内部为白色，蓝黑色斑纹，有颗粒感。可以作为佐餐干酪，也非常适合拌在沙拉中。<br />\r\n	安拉乳品集团的历史可以追溯到1881年，总部位于哥本哈根，该公司主要在北欧地区销售全系列乳制品，并向世界其它国家出口高品质的奶酪、黄油和婴幼儿营养品等产品。2012年，安拉食品公司与德国和英国的乳品公司合并，成为欧洲规模最大的乳业公司，也是全球最大的合作社性质的乳品公司。</p>\r\n<span class=\"STYLE1\">食用方法</span>\r\n<p>\r\n	可配以水果直接食用，或用于制作三明治及其他西式菜。</p>', '1', '2017-03-19 00:09:27', '563', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20162115', '1', '');
INSERT INTO `goods` VALUES ('564', '88', '安拉金文不芝士', '0', '34.80', '830100000', '125克', '', '', '<style type=\"text/css\">\r\n.STYLE1 {\r\n	font-size: 18px;\r\n	font-weight: bold;\r\n	color: #f7941d;}\r\n.STYLE2 {\r\n	font-weight: bold;}\r\n.STYLE3 {\r\n	font-size: 14px;\r\n	font-weight: bold;\r\n	color: rgb(204, 51, 51);}</style>\r\n<p>\r\n	<span class=\"STYLE2\">产地：</span>丹麦<br />\r\n	<span class=\"STYLE2\">奶源：</span>牛奶<br />\r\n	<span class=\"STYLE2\">类型：</span>软质干酪<br />\r\n	<span class=\"STYLE2\">脂肪含量：</span>25%</p>\r\n<span class=\"STYLE1\">商品描述</span>\r\n<p>\r\n	白色的安拉金文不芝士，黄油般的柔软质地，拥有温和柔顺的口感,，是非常好的佐餐芝士。</p>\r\n<span class=\"STYLE1\">品牌故事</span>\r\n<p>\r\n	安拉乳品集团的历史可以追溯到1881年，总部位于哥本哈根，该公司主要在北欧地区销售全系列乳制品，并向世界其它国家出口高品质的奶酪、黄油和婴幼儿营养品等产品。2012年，安拉食品公司与德国和英国的乳品公司合并，成为欧洲规模最大的乳业公司，也是全球最大的合作社性质的乳品公司。</p>\r\n<span class=\"STYLE1\">食用方法</span>\r\n<p>\r\n	可搭配新月形面包、薄脆饼干。</p>', '1', '2017-03-19 00:09:27', '564', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20045746', '1', '');
