INSERT INTO `goods` VALUES ('580', '89', '艾美牌埃玛达尔奶酪', '0', '61.00', '833600000', '200g', '', '', '<style type=\"text/css\">\r\n.STYLE1 {\r\n	font-size: 18px;\r\n	font-weight: bold;\r\n	color: #f7941d;}\r\n.STYLE2 {\r\n	font-weight: bold;}\r\n.STYLE3 {\r\n	font-size: 14px;\r\n	font-weight: bold;\r\n	color: rgb(204, 51, 51);}</style>\r\n<p>\r\n	<span class=\"STYLE2\">产地：</span>瑞士中部地区<br />\r\n	<span class=\"STYLE2\">奶源：</span>牛奶<br />\r\n	<span class=\"STYLE2\">类型：</span>硬质干酪<br />\r\n	<span class=\"STYLE2\">脂肪含量：</span>31%</p>\r\n<span class=\"STYLE1\">商品描述</span>\r\n<p>\r\n	埃玛达尔奶酪原产于瑞士的硬质干酪，有着与时间一同蔓延的魅力，随着慢慢成熟的过程，细致的坚果味道越来越浓郁，而干酪上的空隙也会逐渐变大。埃玛达尔奶酪起源于十三世纪艾姆流域的山谷里，被视为经典瑞士奶酪。需要四个月到一年时间才能成熟的艾玛达尔干酪的味道会随着时间由温和渐渐向浓烈转变，空隙也会变大。如今埃玛达尔奶酪仍然根据450多年前的传统食谱手工制作。</p>\r\n<span class=\"STYLE1\">品牌故事</span>\r\n<p>\r\n	艾美公司是欧洲众所周知的瑞典乳品制造商，以甘美的瑞士风格酸奶和精心巧制充满温暖匠心的干酪而闻名。艾美依偎在瑞士阿尔卑斯山脉腹地之间，为人们带来味觉上顶级盛宴的最高质量标准就源自这里。最正宗的瑞士传统食谱和当地的优质奶源，成就了艾美干酪与众不同的芬芳和正宗瑞士风味。</p>\r\n<span class=\"STYLE1\">食用方法</span>\r\n<ol>\r\n	<li>\r\n		佐餐干酪，焙烤食用，融化后涂抹用。</li>\r\n	<li>\r\n		与肉一起在冷三明治中搭配。</li>\r\n	<li>\r\n		磨碎后撒入蔬菜、汤或沙拉中调味，还可以作为干酪甜点。</li>\r\n</ol>', '1', '2017-03-19 00:09:29', '580', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20482077', '1', '');
