INSERT INTO `goods` VALUES ('706', '112', '美式原味烤鸡(整只熟)', '0', '38.00', '310000000', '只', '', '', '<link href=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/css/desctiption.css\" rel=\"stylesheet\" />\r\n<div id=\"desc-container\">\r\n	<div class=\"section\">\r\n		<h4 class=\"st\">\r\n			选料优质肉鸡</h4>\r\n		<p>\r\n			城市超市的风味电烤鸡选料于饲养天数为60日的优质肉鸡，洗净后每只重约1斤半至2斤，适中的体型更易于腌制入味，肉质柔嫩，烘烤之后依然能保持鲜嫩口感。</p>\r\n	</div>\r\n	<div class=\"section\">\r\n		<h4 class=\"st\">\r\n			独家秘制腌料</h4>\r\n		<p>\r\n			肉鸡必须提前用我们秘制的酱料腌制一晚，除传统的辛香料外，更添加了从美国进口的保尔厨师系列调味料，为鸡肉增加独特而地道的风味。</p>\r\n	</div>\r\n	<div class=\"section\">\r\n		<h4 class=\"st\">\r\n			慢火烘烤</h4>\r\n		<p>\r\n			肉鸡在腌制一夜后，送入85摄氏度左右的烤箱内烤制1小时左右。长时间的慢火烘烤能更多得逼出皮下油脂，皮酥脆带有焦香，入口不腻。</p>\r\n	</div>\r\n	<div class=\"section\">\r\n		<h4 class=\"st\">\r\n			亲民价格十年不变</h4>\r\n		<p>\r\n			我们的风味电烤鸡依然保持着十年前38元每只的价格不变，如此十年不涨价的商品在上海滩已是凤毛麟角。尽管成本飞涨，电烤鸡只剩微利，但为了回馈消费者的信赖与喜爱，我们愿意不计利润，让城市超市的电烤鸡成为每个上海人心中最好吃又价格亲民的&ldquo;明星&rdquo;烤鸡。</p>\r\n	</div>\r\n</div>', '1', '2017-03-19 00:10:06', '706', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20326487', '1', '');
