INSERT INTO `goods` VALUES ('713', '114', '迷你澳洲牛肉汉堡(原味)', '0', '4.00', '310000000', '1只', '', '', '<link href=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/css/desctiption.css\" rel=\"stylesheet\" />\r\n<div id=\"desc-container\">\r\n	<h4 class=\"st\">\r\n		赏味有道</h4>\r\n	<p>\r\n		由城市超市精心制作的迷你澳洲牛肉汉堡（原味），由高品质澳洲牛臀肉牛肉糜制成，不含任何添加剂，不仅美味可口，而且营养健康。</p>\r\n	<h4 class=\"st\">\r\n		<img alt=\"City Tips\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/citytips.jpg\" /></h4>\r\n	<p>\r\n		烤汉堡时，既可以用铝箔纸托着烤制，也可直接在烧烤架烤制。烤前可以选择撒些椒盐，涂抹少许油或者根据个人口味烤完后调味，但要注意，在烤架上烤制时须事先在架子上轻轻刷一层橄榄油，防止食物粘上去，但切勿刷抹过多，以免滴落于炭火上产生焦黑的油烟。烤肉时，可根据个人喜欢的牛肉熟度来把握烧烤时间。烤熟后根据个人口味浇上黑胡椒酱或者其它烧烤酱风味更佳。</p>\r\n</div>', '1', '2017-03-19 00:10:09', '713', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20896584', '1', '');
INSERT INTO `goods` VALUES ('714', '114', '迷你澳洲牛肉汉堡(辣味)', '0', '4.00', '310000000', '块', '', '', '<link href=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/css/desctiption.css\" rel=\"stylesheet\" />\r\n<div id=\"desc-container\">\r\n	<h4 class=\"st\">\r\n		赏味有道</h4>\r\n	<p>\r\n		由城市超市精心制作的迷你澳洲牛肉汉堡（辣味），由高品质澳洲牛臀肉牛肉糜、洋葱碎和青椒碎混合制成，不含任何添加剂，不仅美味可口，而且营养健康。</p>\r\n	<h4 class=\"st\">\r\n		<img alt=\"City Tips\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/citytips.jpg\" /></h4>\r\n	<p>\r\n		烤汉堡时，既可以用铝箔纸托着烤制，也可直接在烧烤架烤制。但要注意，在烤架上烤制时须事先在架子上轻轻刷一层橄榄油，防止食物粘上去，但切勿刷抹过多，以面滴落于炭火上产生焦黑的油烟。烤肉时，可根据个人喜欢的牛肉熟度来把握烧烤时间。烤熟后根据个人口味浇上黑胡椒酱或者其它烧烤酱风味更佳。</p>\r\n</div>', '1', '2017-03-19 00:10:09', '714', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20896591', '1', '');
