INSERT INTO `goods` VALUES ('717', '114', '城市意式辣味牛肉肠', '0', '178.00', '833100000', 'kg', '', '', '<link href=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/css/desctiption.css\" rel=\"stylesheet\" />\r\n<div id=\"desc-container\">\r\n	<h4 class=\"st\">\r\n		赏味有道</h4>\r\n	<p>\r\n		由城市超市精心制作的金伯利红牌澳洲牛肉肠原味，采用高品质的澳洲牛肉和进口牛肉调味粉制成，不仅美味可口，而且营养健康。</p>\r\n	<img alt=\"City Tips\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/citytips.jpg\" />\r\n	<p>\r\n		肉肠既可以用铝箔纸托着烤制，也可直接在烧烤架烤制，但要注意，在烤架上烤制时须事先在架子上轻轻刷一层橄榄油，防止食物粘上去，但切勿刷抹过多，以免滴落于炭火上产生焦黑的油烟。待牛肉肠正反面翻烤至金黄，完全烤熟后可根据个人口味涂抹上相应酱料或是撒些胡椒粉，同样美味。</p>\r\n</div>', '1', '2017-03-19 00:10:09', '717', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20598990', '1', '');
