INSERT INTO `goods` VALUES ('1063', '152', '富维克天然矿泉水', '0', '6.80', '832700000', '500毫升', '', '', '<div class=\"section\">\r\n	<h4 class=\"subtitle\">\r\n		商品特色</h4>\r\n	<p>\r\n		富维克天然矿泉水源自法国奥弗涅地区火山自然公园，泉水透过火山灰、玄武岩和安山岩组成的过滤层，使其成分稳定均衡，矿物质含量低却含有稀有的微量元素，天然纯净，口感温和清爽。</p>\r\n</div>\r\n<div class=\"section\">\r\n	<img alt=\"健康饮水小贴士\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/watertips-cn.jpg\" />\r\n	<h4 class=\"subtitle\">\r\n		品牌介绍</h4>\r\n	<p>\r\n		富维克天然矿泉水，水源来自法国中央高原圆顶火山多姆山正北面的奥弗涅地区火山自然公园。矿泉水内的独特矿物质来自公元前5760年临近火山的最后一次喷发。1922年富维克镇首次利用在古雷山谷的温泉水，1938年首支富维克樽装水面世，现今富维克矿泉水已国际知名。富维克每年生产超过10亿瓶樽装水，在全球60多个国家有售。</p>\r\n</div>', '1', '2017-03-19 00:11:07', '1063', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20001940', '1', '');
INSERT INTO `goods` VALUES ('1064', '152', '斐济矿泉水', '0', '26.00', '831900000', '1000毫升', '', '', '<div class=\"section\">\r\n	<h4 class=\"subtitle\">\r\n		商品特色</h4>\r\n	<p>\r\n		享誉全球的FIJI Water斐泉，是由斐济群岛天然自流水的水源处直接装瓶的世界顶级瓶装水。斐济水生来就富含二氧化硅、钙和镁，自然纯净，不含人工添加物。斐济矿泉水独特的纯净口感会增添食物的绝佳口感，通过与美食的搭配，它不会改变食物的口味，而是籍此将食材的原味更好的提升。因其无比滑顺的口感，斐济矿泉水已成为世界各地知名主厨、美食家和明星名流的御用瓶装水品牌。</p>\r\n</div>\r\n<div class=\"section\">\r\n	<img alt=\"健康饮水小贴士\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/watertips-cn.jpg\" />\r\n	<h4 class=\"subtitle\">\r\n		品牌介绍</h4>\r\n	<p>\r\n		偏远的Viti Levu岛上的Yaqara河谷中，有一片原始热带雨林，雨林的边缘由远古火山岩壁包围而成的巨型火山里有着一片广阔的自流水层，这就是FIJI Water的来源。</p>\r\n	<p>\r\n		斐济矿泉水来自于世界上硕果仅存的原生生态环境区，雨林中的雨水经火山岩层数百年的过滤之后，将最重要的矿物质存留下来，使得斐济矿泉水拥有特殊轻柔滑顺的口感。天然压力使地底深处含水层中的斐济矿泉水涌泉流出，经过密封系统将水运送到独特的方形瓶中，以最纯净和自然的形态呈送给您。</p>\r\n</div>', '1', '2017-03-19 00:11:07', '1064', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20611217', '1', '');
