INSERT INTO `goods` VALUES ('1078', '155', '雀巢飘蓝', '0', '1.80', '120000000', '550毫升', '', '', '<br />\r\n<img alt=\"\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/nestle_c.jpg\" style=\"width: 500px; height: 195px;\" /><br />\r\n<br />\r\n\r\n<style type=\"text/css\">\r\n.STYLE1 {\r\n	font-size: 18px;\r\n	font-weight: bold;\r\n	color: #f7941d;}</style>\r\n<span class=\"STYLE1\">商品特色</span>\r\n<p>\r\n	雀巢飘蓝又称雀巢优活，它源自地下深层水，水质天然纯净，口感清冽爽口。雀巢位于法国的水研究中心拥有非常先进的水质研究体系和水质分析设备，确保了每一瓶雀巢优活都拥有天然、健康、纯净的品质。</p>\r\n<span class=\"STYLE1\">品牌介绍</span>\r\n<p>\r\n	雀巢公司由亨利&bull;内斯特莱（Henri Nestle)于1867年创建，总部设在瑞士日内瓦湖畔的沃韦(Vevey)，在全球拥有500多家工厂，为世界上最大的食品制造商。雀巢优活是雀巢水业集团的核心品牌，在世界瓶装水市场中高居榜首地位。作为闻名世界的国际品牌，雀巢优活始终致力于为消费者提供安全、健康的饮用水。</p>', '1', '2017-03-19 00:11:12', '1078', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20001698', '1', '');
