INSERT INTO `goods` VALUES ('1090', '156', '汉森牌樱桃香草味汽水', '0', '8.80', '833800000', '354毫升', '', '', '<style type=\"text/css\">\r\n.STYLE1 {\r\n	font-size: 18px;\r\n	font-weight: bold;\r\n	color: #f7941d;}</style>\r\n<span class=\"STYLE1\">商品特色</span>\r\n<p>\r\n	真正的马达加斯加香荚兰豆和华盛顿樱桃，使这款樱桃香草奶油味苏打水颇具异域风情。它喝起来香气浓郁，饮料中散发着甜樱桃和奶油的香气，口感甜蜜而丰富，余味干净而温暖，让人心旷神怡。</p>\r\n<span class=\"STYLE1\">品牌介绍</span>\r\n<p>\r\n	汉森饮料是美国市场上的高档品牌，公司位于加利福尼亚州科罗钠，其历史可追溯至20世纪30年代，至今已有着超过75年的果汁和30年的苏打水生产经验。从2008年起，汉森做出了一个重大决定：&ldquo;使用真正的蔗糖，对高果糖玉米糖浆说再见！汉森品牌的产品将永远不含防腐剂、咖啡因、钠、人造香精和着色，它们将回归真正的自然味道。&rdquo;汉森正是以其卓越的品质、美味、健康、天然而风行美国。</p>', '1', '2017-03-19 00:11:14', '1090', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20265656', '1', '');
