INSERT INTO `goods` VALUES ('1361', '188', '罗伯多兰牌长相思赛美蓉白葡萄酒', '0', '178.00', '833100000', '750ml', '', '', '<link href=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/css/description.new.css\" rel=\"stylesheet\" />\r\n<style type=\"text/css\">\r\n#desc-wrapper .section-custom{margin-bottom: 25px;}\r\n		#desc-wrapper .section-custom .st{width: 40%;height: 28px;margin-top: 0;margin-bottom: 20px;padding-right: 20px;font-size: 15px;line-height: 28px;color: #fff;text-align: right;background-color: #798b68 ;border-radius: 0 14px 14px 0;box-sizing: boder-box; }\r\n		#desc-wrapper .section-custom .float-img{float: right;margin-top: -97px;margin-left: 20px;}\r\n		#desc-wrapper .section-custom p{margin: 0 10px;}\r\n		#desc-wrapper .wine-meta{display:table;width:100%;}\r\n		#desc-wrapper .wine-meta span{display:table-cell;*display: inline-block;padding:8px 4px;*padding: 8px 20px;font-size:12px;color:white;text-align:center;}\r\n		#desc-wrapper .meta1{background-color:#551536;}\r\n		#desc-wrapper .meta2{background-color:#751032;}\r\n		#desc-wrapper .meta3{background-color:#88324f;}\r\n		#desc-wrapper .meta4{background-color:#b75c7b;}\r\n		#desc-wrapper .meta5{background-color:#a53b51;}\r\n		#desc-wrapper .meta6{background-color:#c75c61;}\r\n		#desc-wrapper .meta7{background-color:#e58586;}\r\n		#desc-wrapper .basic-info{margin-top: 20px;}\r\n		#desc-wrapper .basic-info li .label{color: #fff;background-color: #798b68 ;}\r\n		@media (max-width: 480px){\r\n			#desc-wrapper .section-custom .float-img { float: none;margin-top: 0;margin-left: 0;margin-bottom: 10px; }\r\n			#desc-wrapper .section-custom .st{width: 65%;margin-bottom: 8px;}\r\n		}</style>\r\n<div id=\"desc-wrapper\">\r\n	<div class=\"wine-meta\">\r\n		<span class=\"meta1\">干型白葡萄酒</span><span class=\"meta2\">酒精度：13%vol</span><span class=\"meta3\">长相思，赛美蓉</span><span class=\"meta4\">罗伯多兰酒庄</span></div>\r\n	<div class=\"wine-meta\">\r\n		<span class=\"meta5\">澳大利亚，维多利亚亚拉谷</span><span class=\"meta6\">清爽多汁，层次分明</span><span class=\"meta7\">开瓶即饮</span></div>\r\n	<div class=\"basic-info\">\r\n		<ul>\r\n			<li class=\"last\">\r\n				<span class=\"label\">商品名称：</span><span class=\"txt\">罗伯多兰牌长相思赛美蓉白葡萄酒</span></li>\r\n			<li class=\"last\">\r\n				<span class=\"label\">年份：</span><span class=\"txt\">2012</span></li>\r\n		</ul>\r\n	</div>\r\n	<div class=\"section-custom\">\r\n		<h4 class=\"st\">\r\n			荣誉</h4>\r\n		<p>\r\n			中国Wine100葡萄酒挑战赛银奖</p>\r\n	</div>\r\n	<div class=\"section-custom\">\r\n		<h4 class=\"st\">\r\n			酒评</h4>\r\n		<p>\r\n			浅稻草色，有着草本和热带水果以及西番莲之类的香气。口感层次分明，呈现多汁的柑橘、梨和热带水果风味，酸味明朗、开胃却又不失平衡。</p>\r\n	</div>\r\n	<div class=\"section-custom\">\r\n		<h4 class=\"st\">\r\n			产区介绍</h4>\r\n		<img alt=\"罗伯多兰\" class=\"fullscreen-img\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/RobDolan001.jpg\" />\r\n		<p>\r\n			维多利亚州作为澳大利亚最主要的葡萄酒产区，其历史起源于亚拉谷。亚拉谷坐落在起伏的青山和牧场之中，有着澳大利亚最古老的葡萄园，气候凉爽潮湿，土壤类型丰富，不仅有砂土、砂岩，更有肥沃的火山土。自从1838年第一批葡萄藤开始在此地种植，澳大利亚的酿酒师就开始在此酿造风格轻柔优雅、具有陈酿潜力的葡萄酒。</p>\r\n	</div>\r\n	<div class=\"section-custom\">\r\n		<h4 class=\"st\">\r\n			酒庄介绍</h4>\r\n		<img alt=\"罗伯多兰\" class=\"float-img\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/RobDolan002.jpg\" />\r\n		<p>\r\n			罗伯多兰酒庄是亚拉谷的知名酒庄之一，被詹姆斯&middot;哈利德评为五星级的酒庄。虽然罗伯多兰是小规模的家族酒庄，但所有的葡萄酒均来自家族成员亲手耕耘的土地，无机械化的有机种植方式、自然少干预的酿酒哲学就是家族最重要的座右铭。最为难能可贵的是，在诸多荣誉光环之下，罗伯多兰依然坚持将简单易饮、不过分复杂作为酿酒的最高理念，出品的葡萄酒也通常具有优雅、清爽、细腻柔和的特点。</p>\r\n	</div>\r\n</div>', '1', '2017-03-19 00:12:02', '1361', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20844813', '1', '');
