INSERT INTO `goods` VALUES ('1375', '189', '橡树岭牌黑皮诺红葡萄酒', '0', '178.00', '833100000', '750ml', '', '', '<link href=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/css/description.new.css\" rel=\"stylesheet\" />\r\n<style type=\"text/css\">\r\n#desc-wrapper .section-custom{margin-bottom: 25px;}\r\n		#desc-wrapper .section-custom .st{width: 40%;height: 28px;margin-top: 0;margin-bottom: 20px;padding-right: 20px;font-size: 15px;line-height: 28px;color: #fff;text-align: right;background-color: #f29c9f;border-radius: 0 14px 14px 0;box-sizing: boder-box; }\r\n		#desc-wrapper .section-custom .float-img{float: right;margin-top: 0;margin-left: 20px;}\r\n		#desc-wrapper .basic-info{margin-top: 20px;}\r\n		#desc-wrapper .basic-info li .label{color: #fff;background-color: #f29c9f;}\r\n		@media (max-width: 480px){\r\n			#desc-wrapper .section-custom .float-img { float: none;margin-top: 0;margin-left: 0;margin-bottom: 10px; }\r\n			#desc-wrapper .section-custom .st{width: 65%;margin-bottom: 8px;}\r\n		}</style>\r\n<div id=\"desc-wrapper\">\r\n	<div class=\"wine-meta\">\r\n		<span class=\"meta1\">干型红葡萄酒</span><span class=\"meta2\">酒精度：13%vol</span><span class=\"meta3\">黑皮诺</span><span class=\"meta4\">橡树岭酒庄</span></div>\r\n	<div class=\"wine-meta\">\r\n		<span class=\"meta5\">澳大利亚，维多利亚亚拉谷</span><span class=\"meta6\">饱满</span><span class=\"meta7\">开瓶即饮</span></div>\r\n	<div class=\"basic-info\">\r\n		<ul>\r\n			<li class=\"last\">\r\n				<span class=\"label\">商品名称：</span><span class=\"txt\">橡树岭牌黑皮诺红葡萄酒</span></li>\r\n			<li class=\"last\">\r\n				<span class=\"label\">年份：</span><span class=\"txt\">2012</span></li>\r\n		</ul>\r\n	</div>\r\n	<div class=\"section-custom\">\r\n		<h4 class=\"st\">\r\n			荣誉</h4>\r\n		<p>\r\n			WBW Rating: 92/100<br />\r\n			中国Wine100葡萄酒挑战赛最佳黑皮诺</p>\r\n	</div>\r\n	<div class=\"section-custom\">\r\n		<img alt=\"橡树岭\" class=\"float-img\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/Oakridge002.jpg\" />\r\n		<h4 class=\"st\">\r\n			酒评</h4>\r\n		<p>\r\n			明亮的紫红色，满是樱桃、野草莓与蔓越莓的气味，间或夹杂着一丝淡淡香草香，充满新鲜的红色浆果和黑樱桃的口味。</p>\r\n	</div>\r\n	<div class=\"section-custom\">\r\n		<h4 class=\"st\">\r\n			产区介绍</h4>\r\n		<p>\r\n			维多利亚州作为澳大利亚最主要的葡萄酒产区，其历史起源于亚拉谷。亚拉谷坐落在起伏的青山和牧场之中，有着澳大利亚最古老的葡萄园，气候凉爽潮湿，土壤类型丰富，不仅有砂土、砂岩，更有肥沃的火山土。自从1838年第一批葡萄藤开始在此地种植，澳大利亚的酿酒师就开始在此酿造风格轻柔优雅、具有陈酿潜力的葡萄酒。</p>\r\n	</div>\r\n	<div class=\"section-custom\">\r\n		<h4 class=\"st\">\r\n			酒庄介绍</h4>\r\n		<img alt=\"橡树岭\" class=\"fullscreen-img\" src=\"http://www.cityshop.com.cn/Content/Images/ProductDesc/Oakridge001.jpg\" />\r\n		<p>\r\n			橡树岭酒庄是亚拉谷非常知名的酒庄，被澳洲国宝级的酒评人詹姆斯&bull;哈利德评为最高等级的五星酒庄。作为在这块土地上耕耘了30多年的家族酒庄成员，他们对这块土地充满了敬意和热爱。不同于其他商业化的酒庄，橡树岭的哲学是尽量少的人为干预葡萄的生长和葡萄酒的酿造。酒庄采用完全野生的葡萄酵母和独特的天然重力法来酿酒，不采用人为澄清等工艺，使葡萄酒展现了自然的风韵和葡萄本身的风味。</p>\r\n	</div>\r\n</div>', '1', '2017-03-19 00:12:04', '1375', '0', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '0', '20843984', '1', '');
